        if(cur_sum == 0)
        {
            res.push_back(path);
            return;
        }
        if(cur_sum = candidates.size()) return;
        path.push_back(candidates[si]);
        dfs(si, cur_sum - candidates[si], path, candidates);
        path.pop_back();
        dfs(si + 1, cur_sum, path, candidates);
    }
};

