class Solution {
public:
    vector> insert(vector>& intervals, vector& newInterval) {
        int st = newInterval[0], ed = newInterval[1], si = -1, ei = -1;
        for(int i = 0; i = intervals[ei][0])
                    ed = max(ed, intervals[ei][1]), ei++;
                break;
            }
        }
        // 注意边界问题
        if(si != -1)
        {
            // 有交集的中间直接删掉
            intervals.erase(intervals.begin() + si, intervals.begin() + ei);
            // 插入合并后的新区间
            intervals.insert(intervals.begin() + si, {st, ed});
        }
        else
        {
            // 没找到有交集的 直接插在最后即可
            intervals.push_back(newInterval);
        }
        return intervals;
    }
};

