        dfs(0, -1, path, nums);
        return res;
    }
    void dfs(int u, int last, vector& path, vector& nums)
    {
        if(u == n) res.push_back(path);
        else
        {
            dfs(u + 1, last, path, nums);
            // 和上一个数一样 且上一个数没选-> 不行🚫
            if(u && nums[u - 1] == nums[u] && last != u - 1) return;
            path.push_back(nums[u]);
            dfs(u + 1, u, path, nums);
            path.pop_back(); 
        }
    }
};

