void bfs(int root)
{
    memset(depth, 0x3f, sizeof depth);
    // 哨兵节点 0 指代所有空集的情况
    depth[0] = 0, depth[root] = 1;
    queue q;
    q.push(root);
    while(q.size())
    {
        int t = q.front();
        q.pop();
        for(int i = h[t]; ~i; i = ne[i])
        {
            int j = e[i];
            if(depth[j] > depth[t] + 1)
            {
                q.push(j);
                depth[j] = depth[t] + 1;
                fa[j][0] = t;
                for(int k = 1; k = 0; k--)
    {
        // 哨兵的好处1假如跳出去了，那么depth[a][k] = 0 = depth[b])
            a = fa[a][k];
    }
    if(a == b) return a;
    for(int k = 15; k >= 0; k--)
    {
        // 哨兵的好处2假如两个点都跳出去，那么f[a][k] = f[b][k] = 0 ，就不会执行。
        // 此外 跳到同一层不能判断是最近祖先（可能比LCA还高) 所以要到下一层才行
        if(fa[a][k] != fa[b][k])
        {
            a = fa[a][k];
            b = fa[b][k];
        }
    }
    return fa[a][0];

}

