class Solution {
public:
    int getMinute(string& time)
    {
        int x, y;
        sscanf(time.c_str(), "%d:%d", &x, &y);
        return x * 60 + y;
    }
    int numberOfRounds(string startTime, string finishTime) {
        int startMinute = getMinute(startTime), finishMinute = getMinute(finishTime);
        if(startMinute > finishMinute)
