pair findStrMatchLength(string s)
{
    int cnt = 0, suc = 0, res = INT_MAX, si = -1;
    unordered_map sset, window;
    // 1. 统计次数
    for(auto c : s)
        if(!sset.count(c))
            sset[c] = 1, cnt++;
    // 2. 维护滑动窗口
    for(int l = 0, r = 0; r  sset[s[l]]) window[s[l++]]--;
        if(suc == cnt)
        {
           if(res > r - l + 1)
                res = r - l + 1, si = l;

        }
    }
    return {si, res};
}

