上一个题同样的模型
class Solution {
public:
    int numberOfSubarrays(vector& nums, int k) {
        // 恰好有 k 个奇数数字
        unordered_map hash;
        int tot = 0, ans = 0;
        hash[0] = 1;
        for(int x : nums)
        {
            tot += (x & 1);
            ans += hash[tot - k];
            hash[tot]++;
        }
        return ans;
    }
};

